        $("#file-upload").change(function() {
            var formData = new FormData();
            formData.append('fileUpload', $(this)[0].files[0]);
            formData.append('property_id', $("#property-id").val());
            formData.append('_token', '{{ csrf_token() }}');

            $.ajax({
                url: '{{ route('upload') }}',
                type: 'POST',
                data: formData,
                contentType: false,
                processData: false,
                success: function(response) {
                    var newDocument =
